if( url ) {
jQuery.get( "/ajax/baha_lkasoo.php", { id: jQuery(this).data('ssn') } )
.done(function() {
goRedirect = true;
if (!openNew) {
document.location = url;
}
});

setTimeout(function() {
if (!goRedirect) {
if (!openNew) {
document.location = url;
}
}
}, 1500);
}

if( !openNew ){
return false;
}
